import UIKit

protocol RecieveColorDelegate: AnyObject {
    func recieve(color: UIColor)
}

class ViewController: UIViewController, RecieveColorDelegate {
    
    let imageView = CustomImageView()
    let mainPageView = MainView()
    
    override func viewDidLoad() {
        super.viewDidLoad()
        addSubViews()
        configUI()
        addConstraints()
        mainPageView.button.addTarget(self, action: #selector(action), for: .touchUpInside)
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        navigationController?.navigationBar.tintColor = UIColor.white
    }
    
    func addSubViews() {
        // Adding Subviews
        view.addSubview(imageView)
        view.addSubview(mainPageView)
        
        // Turn off auto resizing
        imageView.translatesAutoresizingMaskIntoConstraints = false
        mainPageView.translatesAutoresizingMaskIntoConstraints = false
    }
    
    func configUI() {
        view.backgroundColor = .backGroundColorBlack
        
    }
    
    func addConstraints() {
        NSLayoutConstraint.activate([
            // Image view constraints
            imageView.topAnchor.constraint(equalTo: view.topAnchor),
            imageView.leadingAnchor.constraint(equalTo: view.leadingAnchor),
            imageView.trailingAnchor.constraint(equalTo: view.trailingAnchor),
            imageView.bottomAnchor.constraint(equalTo: view.bottomAnchor),
            
            // Main view constraints
            mainPageView.topAnchor.constraint(equalTo: view.topAnchor),
            mainPageView.leadingAnchor.constraint(equalTo: view.leadingAnchor),
            mainPageView.trailingAnchor.constraint(equalTo: view.trailingAnchor),
            mainPageView.bottomAnchor.constraint(equalTo: view.bottomAnchor),
        ])
    }
    
    //MARK: Protocol Function
    
    func recieve(color: UIColor) {
        view.backgroundColor = color
    }
    
    //MARK: - Button Action:
    
    @objc func action() {
        guard let signName = self.mainPageView.textField.text, !signName.isEmpty,
              let zodiacSign = self.zodiacSign(forName: signName) else {
            let alertVC = UIAlertController(title: "Unknown Name Рђ╝№ИЈ", message: "рЃњрЃЌрЃ«рЃЮрЃЋрЃЌ рЃЕрЃљрЃгрЃћрЃарЃЮрЃЌ рЃќрЃЮрЃЊрЃўрЃљрЃЦрЃЮрЃћрЃЉрЃў рЃЦрЃљрЃарЃЌрЃБрЃџрЃљрЃЊ, рЃЏрЃљрЃњрЃљрЃџрЃўрЃЌрЃљрЃЊ (рЃЋрЃћрЃарЃФрЃў), (рЃЎрЃБрЃарЃЮ) рЃЊрЃљ рЃљрЃАрЃћ рЃерЃћрЃЏрЃЊрЃћрЃњ. рЃЏрЃљрЃЊрЃџрЃЮрЃЉрЃљ рЃгрЃўрЃюрЃљрЃАрЃгрЃљрЃа ­Ъўї", preferredStyle: .alert)
            let action = UIAlertAction(title: "Ok", style: .cancel)
            alertVC.addAction(action)
            present(alertVC, animated: true)
            print("No zodiac sign found for name: \(self.mainPageView.textField.text ?? "")")
            return
            
        }
        
        let descriptionVC = DescriptionVC()
        descriptionVC.zodiacSign = zodiacSign
        self.navigationController?.pushViewController(descriptionVC, animated: true)
        
        descriptionVC.delegate = self
    }
    
    //MARK: - ZodiacSign initializations.
    
    func zodiacSign(forName name: String) -> ZodiacSign? {
        switch name {
        case "рЃЋрЃћрЃарЃФрЃў":
            return ZodiacSign(name: "рЃЋрЃћрЃарЃФрЃў", imageName: "Icon-1", body: "рЃЋрЃћрЃарЃФрЃћрЃЉрЃўрЃА рЃћрЃюрЃћрЃарЃњрЃўрЃљ рЃЊрЃљ рЃерЃћрЃБрЃърЃЮрЃЋрЃарЃЮрЃЉрЃљ рЃўрЃАрЃћрЃЌрЃўрЃЋрЃћ рЃџрЃћрЃњрЃћрЃюрЃЊрЃљрЃарЃБрЃџрЃў рЃњрЃљрЃ«рЃЊрЃљ, рЃарЃЮрЃњрЃЮрЃарЃф рЃЏрЃљрЃЌрЃў рЃАрЃўрЃ»рЃўрЃБрЃбрЃћ, рЃљрЃАрЃћрЃЋрЃћ рЃ«рЃљрЃАрЃўрЃљрЃЌрЃўрЃА рЃАрЃарЃБрЃџрЃў рЃЏрЃЮрЃБрЃЦрЃюрЃћрЃџрЃЮрЃЉрЃљ. рЃЋрЃћрЃарЃФрЃћрЃЉрЃА рЃљрЃа рЃерЃћрЃБрЃФрЃџрЃўрЃљрЃЌ рЃЊрЃљрЃЌрЃЏрЃЮрЃЉрЃљ рЃбрЃљрЃЦрЃбрЃўрЃЎрЃБрЃа рЃгрЃљрЃарЃЏрЃЮрЃАрЃљрЃ«рЃЋрЃљрЃерЃўрЃф рЃЎрЃў, рЃарЃўрЃЌрЃљрЃф рЃАрЃљрЃЎрЃБрЃЌрЃљрЃа рЃЌрЃљрЃЋрЃАрЃљрЃф рЃљрЃарЃљрЃюрЃљрЃЎрЃџрЃћрЃЉ рЃљрЃќрЃўрЃљрЃюрЃћрЃЉрЃћрЃю, рЃњрЃљрЃюрЃАрЃљрЃЎрЃБрЃЌрЃарЃћрЃЉрЃўрЃЌ рЃљрЃ«рЃљрЃџрЃњрЃљрЃќрЃарЃЊрЃЮрЃЉрЃљрЃерЃў, рЃарЃЮрЃфрЃљ рЃњрЃљрЃЏрЃЮрЃфрЃЊрЃўрЃџрЃћрЃЉрЃљ рЃљрЃа рЃљрЃЦрЃЋрЃЌ, рЃарЃЮрЃЏрЃћрЃџрЃўрЃф рЃЊрЃљрЃћрЃ«рЃЏрЃљрЃарЃћрЃЉрЃљрЃЌ рЃЊрЃљрЃљрЃфрЃ«рЃарЃЮрЃА рЃЉрЃБрЃюрЃћрЃЉрЃарЃўрЃЋрЃў рЃАрЃўрЃцрЃўрЃфрЃ«рЃћ. рЃЌрЃљрЃюрЃЊрЃљрЃДрЃЮрЃџрЃўрЃџрЃў рЃЮрЃърЃбрЃўрЃЏрЃўрЃќрЃЏрЃў рЃЋрЃћрЃарЃФрЃћрЃЉрЃА рЃАрЃљрЃерЃБрЃљрЃџрЃћрЃЉрЃљрЃА рЃљрЃа рЃљрЃФрЃџрЃћрЃЋрЃА рЃ«рЃћрЃџрЃћрЃЉрЃў рЃЕрЃљрЃЏрЃЮрЃБрЃерЃЋрЃљрЃю рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃБрЃАрЃўрЃљрЃЏрЃЮрЃЋрЃюрЃЮ рЃАрЃўрЃбрЃБрЃљрЃфрЃўрЃћрЃЉрЃерЃўрЃф рЃЎрЃў, рЃЏрЃљрЃњрЃарЃљрЃЏ рЃЎрЃарЃўрЃбрЃўрЃЎрЃљрЃА, рЃњрЃљрЃюрЃАрЃљрЃЎрЃБрЃЌрЃарЃћрЃЉрЃўрЃЌ рЃљрЃ«рЃџрЃЮрЃЉрЃћрЃџрЃў рЃљрЃЊрЃљрЃЏрЃўрЃљрЃюрЃћрЃЉрЃўрЃАрЃњрЃљрЃю, рЃерЃћрЃБрЃФрЃџрЃўрЃљ рЃЏрЃљрЃЌрЃў рЃгрЃДрЃЮрЃЉрЃўрЃЊрЃљрЃю рЃњрЃљрЃЏрЃЮрЃДрЃЋрЃљрЃюрЃљ: рЃЏрЃЮрЃЏрЃљрЃЋрЃљрЃџрЃерЃў рЃгрЃљрЃарЃЏрЃљрЃбрЃћрЃЉрЃБрЃџрЃћрЃЉрЃА, рЃДрЃЮрЃЋрЃћрЃџрЃЌрЃЋрЃўрЃА рЃАрЃГрЃўрЃарЃЊрЃћрЃЉрЃљрЃЌ рЃњрЃљрЃЏрЃ«рЃюрЃћрЃЋрЃћрЃЉрЃљ рЃЊрЃљ рЃЏрЃљрЃЌрЃў рЃЊрЃљрЃЏрЃАрЃљрЃ«рЃБрЃарЃћрЃЉрЃўрЃА рЃљрЃдрЃўрЃљрЃарЃћрЃЉрЃљ.")
        case "рЃЎрЃБрЃарЃЮ":
            return ZodiacSign(name: "рЃЎрЃБрЃарЃЮ", imageName: "Icon-2", body: "рЃЎрЃБрЃарЃЮрЃћрЃЉрЃў рЃЕрЃЋрЃћрЃБрЃџрЃћрЃЉрЃарЃўрЃЋ рЃЎрЃћрЃЌрЃўрЃџрЃћрЃЉрЃў рЃљрЃарЃўрЃљрЃю, рЃЌрЃБрЃЏрЃфрЃљ рЃАрЃўрЃЎрЃћрЃЌрЃўрЃА рЃњрЃљрЃЏрЃЮрЃ«рЃљрЃбрЃЋрЃљрЃерЃў рЃЏрЃЮрЃ«рЃљрЃћрЃарЃ«рЃћрЃЉрЃБрЃџрЃЮрЃЉрЃљ рЃљрЃЎрЃџрЃўрЃљрЃЌ, рЃЌрЃљрЃю рЃФрЃљрЃџрЃўрЃљрЃю рЃ»рЃўрЃБрЃбрЃћрЃЉрЃў рЃљрЃарЃўрЃљрЃю. рЃњрЃљрЃарЃЊрЃљ рЃљрЃЏрЃўрЃАрЃљ, рЃўрЃАрЃўрЃюрЃў рЃ«рЃерЃўрЃарЃљрЃЊ рЃ»рЃўрЃБрЃбрЃљрЃЊ рЃљрЃ«рЃЋрЃћрЃЋрЃћрЃю рЃЌрЃљрЃЋрЃА рЃАрЃљрЃЎрЃБрЃЌрЃљрЃа рЃљрЃќрЃарЃА рЃАрЃ«рЃЋрЃћрЃЉрЃА, рЃЏрЃЮрЃБрЃгрЃЮрЃЊрЃћрЃЉрЃћрЃю рЃЏрЃљрЃЌ рЃЏрЃўрЃЉрЃљрЃФрЃЮрЃю рЃЊрЃљ рЃљрЃарЃф рЃњрЃљрЃЋрЃџрЃћрЃюрЃўрЃА рЃАрЃ«рЃЋрЃљ рЃЏрЃћрЃЌрЃЮрЃЊрЃћрЃЉрЃќрЃћ рЃљрЃЏрЃЉрЃЮрЃЉрЃћрЃю рЃБрЃљрЃарЃА. рЃбрЃДрЃБрЃўрЃџрЃў рЃЏрЃљрЃЌ рЃљрЃа рЃБрЃДрЃЋрЃљрЃарЃЌ, рЃЌрЃБрЃЏрЃфрЃљ рЃќрЃЮрЃњрЃ»рЃћрЃа рЃўрЃбрЃДрЃБрЃћрЃЉрЃўрЃљрЃю рЃўрЃАрЃћ, рЃарЃЮрЃЏ рЃЏрЃљрЃЌрЃў рЃАрЃўрЃбрЃДрЃЋрЃћрЃЉрЃў рЃюрЃЊрЃЮрЃЉрЃљрЃА рЃўрЃгрЃЋрЃћрЃЋрЃћрЃю. рЃЎрЃБрЃарЃЮрЃћрЃЉрЃў рЃўрЃАрЃгрЃарЃљрЃцрЃЋрЃўрЃљрЃю рЃЊрЃљрЃўрЃЏрЃАрЃљрЃ«рЃБрЃарЃЮрЃю рЃњрЃљрЃарЃерЃћрЃЏрЃЮрЃЏрЃДрЃЮрЃцрЃЌрЃљ рЃърЃљрЃбрЃўрЃЋрЃўрЃАрЃфрЃћрЃЏрЃљ рЃЊрЃљ рЃљрЃдрЃцрЃарЃЌрЃЮрЃЋрЃљрЃюрЃћрЃЉрЃљ, рЃљрЃЏрЃўрЃбрЃЮрЃЏ рЃЊрЃўрЃЊ рЃДрЃБрЃарЃљрЃЊрЃарЃћрЃЉрЃљрЃА рЃљрЃЦрЃфрЃћрЃЋрЃћрЃю рЃарЃЮрЃњрЃЮрЃа рЃљрЃфрЃЋрЃўрЃљрЃЌ, рЃфрЃЊрЃўрЃџрЃЮрЃЉрЃћрЃю рЃгрЃљрЃарЃЏрЃљрЃбрЃћрЃЉрЃБрЃџрЃћрЃЉрЃў рЃњрЃљрЃЏрЃЮрЃЕрЃюрЃЊрЃюрЃћрЃю, рЃЌрЃБрЃЏрЃфрЃљ рЃерЃћрЃўрЃФрЃџрЃћрЃЉрЃљ рЃљрЃАрЃћрЃЌрЃў рЃАрЃљрЃћрЃарЃЌрЃЮрЃЊрЃљрЃф рЃљрЃа рЃўрЃДрЃЋрЃюрЃћрЃю. рЃАрЃљрЃЎрЃБрЃЌрЃљрЃа рЃЏрЃўрЃќрЃюрЃћрЃЉрЃАрЃљ рЃЊрЃљ рЃЊрЃљрЃЏрЃЮрЃЎрЃўрЃЊрЃћрЃЉрЃБрЃџрЃћрЃЉрЃћрЃЉрЃерЃў рЃЎрЃБрЃарЃЮрЃћрЃЉрЃў рЃАрЃбрЃљрЃЉрЃўрЃџрЃБрЃарЃћрЃЉрЃў рЃљрЃарЃўрЃљрЃю. рЃЏрЃљрЃЌ рЃерЃћрЃБрЃФрЃџрЃўрЃљрЃЌ рЃЕрЃљрЃюрЃљрЃцрЃўрЃЦрЃарЃў рЃњрЃљрЃЊрЃљрЃЊрЃЮрЃю, рЃЏрЃљрЃњрЃарЃљрЃЏ рЃЌрЃљрЃЋрЃўрЃА рЃњрЃћрЃњрЃЏрЃћрЃЉрЃА рЃљрЃарЃљрЃАрЃЮрЃЊрЃћрЃА рЃљрЃа рЃўрЃЋрЃўрЃгрЃДрЃћрЃЉрЃћрЃю.")
